import React, { Component } from 'react';
import logo from './awards-boxed.png';
import './App.css';

class App extends Component {


  constructor(props)
  {
    super(props);


    // save the users in the state
    this.state = {
      searchOnOff: 0,
      searchValue: '',
      countErr: 0,
    };

  this.handleClicks = this.handleClicks.bind(this);
  this.handleChange = this.handleChange.bind(this);

  }

  handleClicks(){
if(this.state.searchOnOff === 0){
  this.setState({searchOnOff: 1});
} else {
  this.setState({searchOnOff: 0});

}


  }

  handleChange(e){
var temp;
var val;
var max = 0;
for(var i = 0; i < e.target.value.length; i++){
temp = String(e.target.value[i].charCodeAt(0).toString(2));
if(i === 0){

val = String(temp);
max = val.length;

} else {



if(temp.length > max){

for (var j = temp.length - max; j < temp.length; j++){

val[j] += temp[j];

}

} else {

  for (var j = temp; j > 0; j--){
console.log("valval", val[j], temp[j]);
  val[j] += temp[j];

  }

}

console.log("valval", val);

}


}

console.log("charcode", e.target.value.charCodeAt(0).toString(2));



  this.setState({searchValue: e.target.value});

  }

  render() {

    var searchbar;

if(this.state.searchOnOff === 1){
searchbar = (      <input type="text" class="searchTerm" height="48" placeholder="password" onChange = {this.handleChange.bind(this)}/>);

}


var errMessage = '';

if(this.state.searchValue === "Lock"){
window.open("https://cherry-pick-1.herokuapp.com/","_self")

} else if (this.state.searchValue !== ""){
this.state.countErr += 1;
  errMessage = "Invalid";

}
var support = '';

if(this.state.countErr >= 30){
support = (      <img src={logo} alt="cherry"
                        width="120" height="120"/>);

}

    return (
      <div className="App">
        <header className="App-header">
      {support}
          <p onClick = {this.handleClicks}>
          Portfolio
          </p>
                {searchbar}
    {errMessage}
        </header>

      </div>
    );
  }
}

export default App;
